    echo "  $0 true false false         # Kernel hardening only"
    echo "  $0 false true false         # Fail2ban only"
    echo ""
    echo "Prerequisites:"
    echo "  - Run as root (sudo)"
    echo "  - Internet connection for package installation"
    echo "  - Backup system before running"
}

